<?phpdeclare(strict_types = 1);function insert_logo() {    echo '<img id="candy_logo" src="../img/candy_logo.png" alt="candy logo" height="30px">';}function insert_message() {    $weekday = date('l');    $dayOfMonth = date('d');    $month = date('F');    $year = date('Y');    $message = "Welcome!!! <br>Today is $weekday $month $year";     return $message;}$tax = '18';function calculate_total(float $price,float $quantity): float {    $cost = $price * $quantity;    $tax = $cost * 0.18;    $total = $cost + $tax;    return $total;}$tax_rate = 0.18;function calculate_running_total($price,$quantity) {    global $tax_rate;    $cost = $price * $quantity;    $tax = $cost * $tax_rate;    $total = $cost + $tax;    return $total;}$us_price = 1.75;$rates =[    'uk' => 0.81,    'eu' => 0.93,    'jp' => 113.21,];function calculate_global_prices($usd, $exchange_rates) {    $prices = [        'pound' => $usd * $exchange_rates['uk'],        'euro' => $usd * $exchange_rates['eu'],        'yen' => $usd * $exchange_rates['jp'],    ];    return $prices;}$global_prices = calculate_global_prices($us_price,$rates);?><!DOCTYPE html><?php require_once '../includes/chapters_header.php'; ?><body><?php require_once '../includes/chapters_navbar.php'; ?><main> <menu>        <div class="sidebar">            <h1>PHP & MY SQL Book</h1>            <h2 class="sidebar_heading">Section A: Basic Programming Instructions</h2>            <ul>                <li class="chapter"><a href="introduction.php" id="A:Introduction">Introduction</a></li>                <li class="chapter"><a href="chapter1.php" id="Chapter1">Chapter 1: Variables, Expressions and</br>                    Operators</a></li>                <li class="chapter"><a href="chapter2.php" id="Chapter2">Chapter 2: Control Structures</a></li>                <li class="chapter selected"><a href="chapter3.php" id="Chapter3">Chapter 3: Functions</a></li>                <li class="chapter"><a href="#" id="Chapter4">Chapter 4: Objects</a></li>            </ul>            <h2 class="sidebar_heading">Section B: Dynamic Web Pages</h2>            <ul>                <li class="chapter"><a href="#" id="B:Introduction">Introduction</a></li>                <li class="chapter"><a href="#" id="Chapter5">Chapter 5: Built-in Functions</a></li>                <li class="chapter"><a href="#" id="Chapter6">Chapter 6: Getting Data From Browsers</a></li>                <li class="chapter"><a href="#" id="Chapter7">Chapter 7: Images and Files</a></li>                <li class="chapter"><a href="#" id="Chapter8">Chapter 8: Dates and Times</a></li>                <li class="chapter"><a href="#" id="Chapter9">Chapter 9: Cookies and Sessions</a></li>                <li class="chapter"><a href="#" id="Chapter10">Chapter 10: Error Handling</a></li>            </ul>        </div>    </menu>    <div class="main">        <h1><?= insert_logo();?>The Candy Store <?php insert_logo();?></h1>        <h2><?= insert_message();?></h2>        <br>        <h3>Order Totals</h3>        <p>Mints €<?= calculate_total(2,5);?></p>        <p>Chocolates €<?= calculate_total(3.75,7);?></p>        <p>Cookies €<?= calculate_total(1.25,10);?></p>        <p>Prices are calculated using <?= $tax?>% tax.</p>        <br>        <table width="200" border="1">            <tbody>                <tr>                    <th>Item</th>                    <th>Price</th>                    <th>Qty</th>                    <th>Running Total</th>                </tr>                <tr>                    <td>Mints</td>                    <td>€2</td>                    <td>5</td>                    <td>€<?= calculate_running_total(2,5); ?></td>                </tr>                <tr>                    <td>Toffee</td>                    <td>€3</td>                    <td>5</td>                    <td>€<?= calculate_running_total(3,5); ?></td>                </tr>                <tr>                    <td>Fudge</td>                    <td>€5</td>                    <td>4</td>                    <td>€<?= calculate_running_total(5,4)?></td>                </tr>            </tbody>        </table>        <br>        <h2>Global Prices for Chocolate</h2>        <p>US $<?= $us_price ?></p>        <p>UK &pound;<?= $global_prices['pound'] ?> | EU €<?= $global_prices['euro']?> | JP &yen;<?= $global_prices['yen']?></p>            </div></main><?php include '../includes/footer.php'; ?></body></html>